import { Injectable } from '@angular/core';
import { Login, Response } from '../model/interfaces.interface';
import { HttpClient,HttpHeaders } from '@angular/common/http';
import { environment, EnvironmentService } from 'src/enviroment/enviroment';
import { Observable } from 'rxjs';


const URL = environment.url;

@Injectable({
  providedIn: 'root'
})
export class ApiService {
  constructor(private http:HttpClient,
    private environmentService: EnvironmentService) {


  }
  login(form:Login):Observable<any>{
    let direccion = `${URL}/api/Auth`
    return this.http.post(direccion,{
      Email : form.Email,
      Password : form.Password
    });
  }


  dashboard(){
    let direccion = `${URL}/api/Dashboard`
    return this.http.get(direccion,{
      headers : new HttpHeaders({
        'Authorization': this.environmentService.apiToken
      })
    })
  }
}
