import { Component } from '@angular/core';

import { FormControl, FormGroup, Validators } from '@angular/forms';

import { ApiService } from 'src/app/services/api.service';
import { Login } from 'src/app/model/login.interface';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.scss']
})
export class LoginComponent {

  loginForm = new FormGroup({
    user : new FormControl('Inserte su correo',Validators.required),
    password: new FormControl('Inserte su contrasela', Validators.required)
  })

  constructor(private api: ApiService){}

  onSubmit(form:Login){
    this.api.login(form).subscribe(data =>{
      console.log(data)
    });
  }
}

<div>
  <h1>Login</h1>
  <div>
    <form [formGroup]="loginForm" (ngSubmit)="onSubmit(loginForm.value)">
      <p>Usuario</p>
      <input type="text" placeholder="Ingrese su correo aqui" formControlName="usuario"/>
      <p>Contraseña</p>
      <input type="password" placeholder="Inserte aqui su contraseña" formControlName="password" />
      <button type="submit">Iniciar sesión</button>
    </form>

  </div>
</div>
